CreateDb.SqlCreateTable:
create table if not exists tb_photo(
time TEXT,
path TEXT,
to_user TEXT,
sendTime TEXT
);

UpdateDb.SqlAfter:
insert into tb_photo(time, path)
select time,path
from back_tb_photo;

insert into tb_user(name,password,state,userId)
select name,password,state,userId
from back_tb_user;
